mapper.update:
INSERT INTO TRADE(TRADE_NO, TRADE_STATUS_NO, BID_NO, AUCTION_NO, BIDDER_NICKNAME, SELLER, FINAL_BID_PRICE) SELECT TRADE_SEQ.NEXTVAL, T.* FROM(
SELECT 1 AS TRADE_STATUS_NO, #{item.bid_no} AS BID_NO, #{item.auction_no} AS AUCTION_NO, #{item.bidder_nickname} AS BIDDER_NICKNAME, #{item.seller} AS SELLER, #{item.final_bid_price} AS FINAL_BID_PRICE FROM SYS.DUAL
) T

mapper.select:
	SELECT FINAL_BID_PRICE
    FROM TRADE
  	WHERE AUCTION_NO IN

  			  	#{auction_no}

  		AND BIDDER_NICKNAME = 

  			  	#{nickname}
